        public string GetCoverageNoteByUser(Guid municipalityId)
        {
            if (IsNationalDataset())
            {
                foreach (CoverageDataset coverage in Coverage)
                {
                    if (coverage.MunicipalityId == municipalityId)
                    {
                        return coverage.Note;
                    }
                }
                return null;
            }
            else {
                return Notes;
